import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Dashboard.css';

interface User {
  id: string;
  firstName: string;
  lastName: string;
  email: string;
  role: 'teacher' | 'admin';
  employeeId: string;
  department: string;
}

interface DashboardProps {
  user: User;
}

interface DashboardStats {
  totalClassrooms: number;
  totalSchedules: number;
  pendingSchedules: number;
  todayUsage: number;
  weeklyUtilization: number;
  recentActivities: any[];
}

const Dashboard: React.FC<DashboardProps> = ({ user }) => {
  const [stats, setStats] = useState<DashboardStats>({
    totalClassrooms: 0,
    totalSchedules: 0,
    pendingSchedules: 0,
    todayUsage: 0,
    weeklyUtilization: 0,
    recentActivities: []
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      
      // Fetch classrooms
      const classroomsRes = await axios.get('/api/classrooms');
      const totalClassrooms = classroomsRes.data.length;

      // Fetch schedules
      const schedulesRes = await axios.get('/api/schedules');
      const totalSchedules = schedulesRes.data.length;
      const pendingSchedules = schedulesRes.data.filter((s: any) => s.status === 'pending').length;

      // Fetch today's usage
      const today = new Date().toISOString().split('T')[0];
      const usageRes = await axios.get(`/api/usage?date=${today}`);
      const todayUsage = usageRes.data.length;

      // Fetch weekly utilization
      const weekAgo = new Date();
      weekAgo.setDate(weekAgo.getDate() - 7);
      const weeklyUsageRes = await axios.get(`/api/usage?startDate=${weekAgo.toISOString().split('T')[0]}&endDate=${today}`);
      const weeklyUtilization = weeklyUsageRes.data.length;

      // Recent activities (last 10 schedules and usage records)
      const recentSchedules = schedulesRes.data
        .sort((a: any, b: any) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime())
        .slice(0, 5);
      
      const recentUsage = usageRes.data
        .sort((a: any, b: any) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime())
        .slice(0, 5);

      const recentActivities = [...recentSchedules, ...recentUsage]
        .sort((a: any, b: any) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime())
        .slice(0, 10);

      setStats({
        totalClassrooms,
        totalSchedules,
        pendingSchedules,
        todayUsage,
        weeklyUtilization,
        recentActivities
      });
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="dashboard-loading">
        <div className="loading-spinner"></div>
        <p>Loading dashboard...</p>
      </div>
    );
  }

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>Welcome back, {user.firstName}!</h1>
        <p>Here's what's happening with your classroom utilization system.</p>
      </div>

      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon">üè´</div>
          <div className="stat-content">
            <h3>{stats.totalClassrooms}</h3>
            <p>Total Classrooms</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">üìÖ</div>
          <div className="stat-content">
            <h3>{stats.totalSchedules}</h3>
            <p>Total Schedules</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">‚è≥</div>
          <div className="stat-content">
            <h3>{stats.pendingSchedules}</h3>
            <p>Pending Approvals</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">‚è∞</div>
          <div className="stat-content">
            <h3>{stats.todayUsage}</h3>
            <p>Today's Usage</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">üìä</div>
          <div className="stat-content">
            <h3>{stats.weeklyUtilization}%</h3>
            <p>Weekly Utilization</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">üë§</div>
          <div className="stat-content">
            <h3>{user.role}</h3>
            <p>Your Role</p>
          </div>
        </div>
      </div>

      <div className="dashboard-content">
        <div className="recent-activities">
          <h2>Recent Activities</h2>
          <div className="activity-list">
            {stats.recentActivities.length === 0 ? (
              <p className="no-activities">No recent activities</p>
            ) : (
              stats.recentActivities.map((activity, index) => (
                <div key={index} className="activity-item">
                  <div className="activity-icon">
                    {activity.subject ? 'üìÖ' : '‚è∞'}
                  </div>
                  <div className="activity-content">
                    <p className="activity-title">
                      {activity.subject ? 
                        `Schedule: ${activity.subject} - ${activity.courseCode}` :
                        `Usage: ${activity.classroom?.name}`
                      }
                    </p>
                    <p className="activity-time">
                      {new Date(activity.createdAt).toLocaleString()}
                    </p>
                  </div>
                  <div className="activity-status">
                    <span className={`status-badge ${activity.status || 'active'}`}>
                      {activity.status || 'active'}
                    </span>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>

        <div className="quick-actions">
          <h2>Quick Actions</h2>
          <div className="action-buttons">
            <a href="/schedules" className="action-btn">
              <span className="action-icon">üìÖ</span>
              <span>Request Schedule</span>
            </a>
            <a href="/usage" className="action-btn">
              <span className="action-icon">‚è∞</span>
              <span>Check In/Out</span>
            </a>
            <a href="/reports" className="action-btn">
              <span className="action-icon">üìà</span>
              <span>Generate Report</span>
            </a>
            {user.role === 'admin' && (
              <a href="/monitoring" className="action-btn">
                <span className="action-icon">üëÅÔ∏è</span>
                <span>Monitor System</span>
              </a>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
